module.exports = function(gui) {
    this.menu = new gui.Menu({'type': 'menubar'});
    this.fileMenu = new gui.Menu();
    this.helpMenu = new gui.Menu();
    this.projectMenu = new gui.Menu();

    // help menu part
    this.helpMenu.append(new gui.MenuItem({label: '路由器设置'}));
    this.helpMenu.append(new gui.MenuItem({label: '反馈Bug'}));
    this.helpMenu.append(new gui.MenuItem({label: '关于'}));

    // help menus action
    this.helpMenu.items[0].click = function() {
        gui.Window.open("http://keith3.github.io/drcom/index.html#2");
    };
    this.helpMenu.items[1].click = function() {
        gui.Window.open("https://github.com/keith3/drcom/issues");
    };
    this.helpMenu.items[2].click = function() {
        gui.Window.open("https://github.com/keith3/drcom");
    };

    this.menu.append(new gui.MenuItem(
        {
            label: "Help",
            submenu: this.helpMenu
        }
    ));
};
